parameters.jsCode:
const html = $('Webhook1').first().json.query.Edit_Response;

let plainText = html
  .replace(/<(\/)?(p|div|h[1-6]|li|br)[^>]*>/gi, '\n')  // Convert paragraph/line break tags
  .replace(/<[^>]+>/g, '')                              // Strip any remaining tags
  .replace(/\n{2,}/g, '\n\n')                           // Clean up extra newlines
  .replace(/[ \t]+\n/g, '\n')                           // Clean trailing whitespace
  .replace(/&nbsp;/gi, ' ')
  .replace(/&amp;/gi, '&')
  .replace(/&lt;/gi, '<')
  .replace(/&gt;/gi, '>')
  .trim();

return {
  plainTextEmail: plainText
};

