=<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <title>Edit Email Content</title>
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <style>
    body {
      font-family: "Segoe UI", sans-serif;
      background-color: #eef2f7;
      padding: 30px;
      margin: 0;
    }

    .container {
      max-width: 800px;
      background-color: #ffffff;
      padding: 30px;
      margin: auto;
      border-radius: 12px;
      box-shadow: 0 12px 30px rgba(0, 0, 0, 0.1);
    }

    h2 {
      font-size: 24px;
      color: #333;
      margin-bottom: 20px;
      border-bottom: 1px solid #ccc;
      padding-bottom: 10px;
    }

    .email-header {
      margin-bottom: 20px;
    }

    .email-header label {
      font-weight: bold;
      display: block;
      margin-bottom: 5px;
    }

    .email-header input {
      width: 100%;
      padding: 10px;
      font-size: 16px;
      border: 1px solid #ddd;
      border-radius: 4px;
      box-sizing: border-box;
      background-color: #f5f5f5;
    }

    #emailPreview, #emailEditor {
      white-space: pre-wrap;
      font-size: 16px;
      color: #333;
      background: #f9f9f9;
      padding: 20px;
      border-radius: 8px;
      border: 1px solid #ddd;
      margin-bottom: 20px;
    }

    textarea {
      width: 100%;
      max-width: 100%;
      height: 300px;
      font-size: 16px;
      font-family: inherit;
      padding: 15px;
      border-radius: 8px;
      border: 1px solid #ccc;
      resize: vertical;
      box-sizing: border-box;
    }

    .btn-group {
      display: flex;
      gap: 10px;
      justify-content: flex-end;
      margin-top: 10px;
    }

    button, a.send-link {
      padding: 10px 20px;
      font-size: 15px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      text-decoration: none;
      color: white;
      display: inline-block;
    }

    .edit-btn { background-color: #005a9e; }
    .save-btn { background-color: #0078d4; }
    .send-btn, .send-link { background-color: #107c10; }

    .signature {
      margin-top: 30px;
      text-align: left;
    }

    .signature img {
      width: 150px;
      height: auto;
      margin-top: 10px;
    }

    .popup-box {
      display: none;
      text-align: center;
    }

    .popup-box h2 {
      font-size: 26px;
      color: #2e7d32;
    }

    .popup-box .icon {
      font-size: 48px;
      color: #4caf50;
      margin-bottom: 20px;
    }
  </style>
</head>
<body>
<div class="container">
  <h2 id="mainTitle">EDIT MAIL CONTENT</h2>

  <!-- Email Header -->
  <div class="email-header">
    <label>From: <strong>Agentic AI</strong></label>
    <label for="recipientEmail">To:</label>
    <input type="email" id="recipientEmail" value="{{ $('details').item.json.personal_email }}" readonly />
  </div>

  <!-- Edit Area -->
  <div id="editorSection" style="display: none;">
    <textarea id="emailTextArea">{{ $json.plainTextEmail }}</textarea>
    <div class="btn-group">
      <button class="save-btn" onclick="saveChanges()">Make Changes</button>
    </div>
  </div>

  <!-- Preview -->
  <div id="previewSection">
    <div id="emailPreview">{{ $json.plainTextEmail.replace(/\n/g, '<br>') }}</div>
    <div class="signature">
      <img src="https://media.licdn.com/dms/image/v2/C561BAQFcaBKo0RUV_g/company-background_10000/company-background_10000/0/1584361725954/rencata_cover?e=2147483647&v=beta&t=Om7LLN3Ndu_hg9G5SL9WE6vURmRBSLnv0pHX4XhPPnc" alt="Rencata Logo" />
    </div>
    <div class="btn-group">
      <button class="edit-btn" onclick="editEmail()">Edit</button>
      <button class="send-btn" onclick="showConfirm()">Send</button>
    </div>
  </div>

  <!-- Confirm Preview -->
  <div id="confirmSection" style="display: none;">
    <div id="finalPreview"></div>
    <div class="signature">
      <img src="https://media.licdn.com/dms/image/v2/C561BAQFcaBKo0RUV_g/company-background_10000/company-background_10000/0/1584361725954/rencata_cover?e=2147483647&v=beta&t=Om7LLN3Ndu_hg9G5SL9WE6vURmRBSLnv0pHX4XhPPnc" alt="Rencata Logo" />
    </div>
    <div class="btn-group">
      <a id="sendLink" class="send-link" target="_blank" onclick="showThankYou()">Confirm & Send</a>
    </div>
  </div>

  <!-- Thank You Message -->
  <div id="thankYouBox" class="popup-box">
    <div class="icon">âœ…</div>
    <h2>Thank You!</h2>
    <p>Offer Letter sent to the candidate.</p>
    <p>This window will close automatically.</p>
  </div>
</div>

<script>
  function editEmail() {
    document.getElementById('previewSection').style.display = 'none';
    document.getElementById('editorSection').style.display = 'block';

    const currentHTML = document.getElementById('emailPreview').innerHTML;
    const plainText = currentHTML.replace(/<br\s*\/?>/gi, "\n");
    document.getElementById('emailTextArea').value = plainText;
    document.getElementById('emailTextArea').focus();
  }

  function saveChanges() {
    const edited = document.getElementById('emailTextArea').value;
    document.getElementById('editorSection').style.display = 'none';
    document.getElementById('previewSection').style.display = 'block';
    document.getElementById('emailPreview').innerHTML = edited.replace(/\n/g, "<br>");
  }

  function showConfirm() {
    const final = document.getElementById('emailPreview').innerHTML;
    const plainText = final.replace(/<br\s*\/?>/gi, '\n').replace(/&nbsp;/g, ' ').trim();

    document.getElementById('previewSection').style.display = 'none';
    document.getElementById('confirmSection').style.display = 'block';
    document.getElementById('finalPreview').innerHTML = final;

    // Get the recipient email
    const candidateEmail = document.getElementById('recipientEmail').value;

    // Dynamically set the href with encoded values
    const encodedContent = encodeURIComponent(plainText);
    const encodedEmail = encodeURIComponent(candidateEmail);

    document.getElementById('sendLink').href = `https://192.168.50.120:5670/webhook/Approval?content=${encodedContent}&to=${encodedEmail}`;
  }

  function showThankYou() {
    document.getElementById('mainTitle').style.display = 'none';
    document.getElementById('confirmSection').style.display = 'none';
    document.getElementById('thankYouBox').style.display = 'block';

    setTimeout(() => window.close(), 3000);
  }
</script>

</body>
</html>
