// Get all input items
const items = $input.all();

// Define numeric fields to convert
const numericFields = [
  'fixed_component',
  'variable_component', 
  'basic_salary',
  'hra',
  'other_allowances',
  'pf_amount',
  'esi_amount',
  'gross_salary',
  'net_salary',
  'gratuity',
  'flexi_benefits',
  'total_annual_performance_incentive',
  'pf_company_contribution',
  'annual_ctc'
];

// Convert fields for each item
return items.map(item => {
  const converted = { ...item.json };

  numericFields.forEach(field => {
    if (converted[field] !== undefined && converted[field] !== null && converted[field] !== '') {
      converted[field] = parseFloat(converted[field]);
    }
  });

  return { json: converted };
});

// Get first and last name from previous node
const employeeFirstName = $('Convert Data Types').first().json.first_name;

const employeeLastName = $('Convert Data Types').first().json.last_name;

// Get current date
const now = new Date();

// Normalize date as YYYY-MM-DD
const yyyy = now.getFullYear();
const mm = String(now.getMonth() + 1).padStart(2, '0'); // Months are 0-based
const dd = String(now.getDate()).padStart(2, '0');
const normalizedDate = `${yyyy}-${mm}-${dd}`;

// Build safe file name
const safeFileName = `${employeeFirstName}_${employeeLastName}_${normalizedDate}`
  .replace(/[^a-z0-9_]/gi, '_') // replace any special chars with _
  .toLowerCase();

// Return as json
return [{ json: { safeFileName } }];

// Get candidate data from the IF node (previous node that has the candidate data)
// const candidateData = $('Has Variable Pay?1').first().json;
const employeeName = $('Combined Data Query4').first().json.first_name;
const employeeLastName = $('Combined Data Query4').first().json.last_name;
const safeFileName = $('UpdateFileName').first().json.safeFileName.replace(/[^a-z0-9]/gi, '_').toLowerCase();
const filePath = `/home/node/offertemp/html/offerletter_${safeFileName}.html`;
const htmlContent = $input.first().json.html;

const command = `
mkdir -p /home/node/offertemp/html && cat > ${filePath} << 'EOF'
${htmlContent}
EOF
`;

return {
  json: {
    htmlFilePath: filePath,
    htmlContent: htmlContent,
    command: command.trim(),
    fileName: `offerletter_${safeFileName}.html`,
    candidateName: `${employeeName} ${employeeLastName}`
  }
};

const safeFileName = $('UpdateFileName').first().json.safeFileName;
const htmlPath = `/home/node/offertemp/html/offerletter_${safeFileName}.html`;
const pdfPath = `/home/node/offertemp/pdf/offerletter_${safeFileName}.pdf`;

const convertOfferCommand = `
cd /home/node/offertemp/scripts

echo "=== Converting ${safeFileName}.html to PDF ==="
cat > convert_offer.js << 'EOF'
const puppeteer = require('puppeteer');`
const fs = require('fs');

(async () => {
  try {
    console.log('Starting PDF conversion for ${safeFileName}.html...');

    if (!fs.existsSync('${htmlPath}')) {
      throw new Error('HTML file not found: ${htmlPath}');
    }

    const htmlContent = fs.readFileSync('${htmlPath}', 'utf8');

    const browser = await puppeteer.launch({
      headless: true,
      executablePath: '/usr/bin/chromium',
      args: ['--no-sandbox', '--disable-setuid-sandbox', '--disable-dev-shm-usage', '--disable-gpu']
    });

    const page = await browser.newPage();
    await page.goto('file://${htmlPath}', { waitUntil: 'networkidle0', timeout: 30000 });

    await page.pdf({
      path: '${pdfPath}',
      format: 'A4',
      printBackground: true,
      margin: {
        top: '1cm',
        right: '1cm',
        bottom: '1cm',
        left: '1cm'
      }
    });

    await browser.close();

    if (fs.existsSync('${pdfPath}')) {
      const stats = fs.statSync('${pdfPath}');
      console.log(\`PDF created successfully: \${stats.size} bytes\`);
    } else {
      throw new Error('PDF file was not created');
    }

  } catch (err) {
    console.error('Error during PDF conversion:', err);
    fs.writeFileSync('/home/node/offertemp/pdf/error.log',
      \`Error converting ${safeFileName}.html: \${err.message}\\n\${err.stack}\\n\`,
      { flag: 'a' }
    );
    process.exit(1);
  }
})();
EOF

echo "=== Running conversion ==="
node convert_offer.js

echo "=== Checking result ==="
ls -la ${pdfPath}
`;

return {
  json: {
    command: convertOfferCommand.trim(),
    htmlPath: htmlPath,
    pdfPath: pdfPath
  }
};
